'use strict';

const Pug = require('pug');
const AWS = require('aws');

const dynamo = new AWS.DynamoDB();

let template = `doctype html
html(lang="en")
  head
    title AT&T Alexa Contacts
  body
    form(action="POST")
      label #1
        input(type="text" name="name1" value= name1)
        input(type="tel" name="phone1" value= phone1)
      label #2
        input(type="text" name="name2" value= name2)
        input(type="tel" name="phone2" value= phone2)
      label #3
        input(type="text" name="name3" value= name3)
        input(type="tel" name="phone3" value= phone3)
      label #4
        input(type="text" name="name4" value= name4)
        input(type="tel" name="phone4" value= phone4)
      label #5
        input(type="text" name="name5" value= name5)
        input(type="tel" name="phone5" value= phone5)
      label #6
        input(type="text" name="name6" value= name6)
        input(type="tel" name="phone6" value= phone6)
      label #7
        input(type="text" name="name7" value= name7)
        input(type="tel" name="phone7" value= phone7)
      label #8
        input(type="text" name="name8" value= name8)
        input(type="tel" name="phone8" value= phone8)
      label #9
        input(type="text" name="name9" value= name9)
        input(type="tel" name="phone9" value= phone9)
      label #10
        input(type="text" name="name10" value= name10)
        input(type="tel" name="phone10" value= phone10)
      submit Save`;

const form = pug.compile(template)

exports.handler = function (event, context, callback) {
  callback(null, form({}));
};
